$(function(){impress().init(),$(".hidden").css("opacity",0),window.addEventListener("impress:stepenter",function(a){$(".hidden.active").animate({opacity:1});var e=$(a.target).attr("id");switch(e){case"locos":break;case"preguntas":$("#dispara").css("opacity",1).addClass("animated bounceInUp");break;case"probar":$("#probar p").each(function(){$(this).hasClass("big")||$(this).css("opacity",1).addClass("animated fadeInDown")});break;case"ejemplos":$("#ejemplos img").css("opacity",1).addClass("animated fadeInUp");break;case"ejemplo-html":$("#ejemplo-html img").css("opacity",1).addClass("animated zoomInDown")}}),window.addEventListener("impress:stepleave",function(){$(".hidden.past").animate({opacity:0})})});